mapper.select:
SELECT id
FROM account
WHERE username = #{username}

SELECT id
FROM role
WHERE role_name = #{roles}

mapper.insert:
INSERT INTO account_roles
(
    account_id,
    role_id
)
VALUES
(
    #{accountId},
    #{roleId}
)

mapper.update:
UPDATE account_roles SET
    role_id = #{roleId}
WHERE account_id = #{accountId}
